  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


const layoutProps = {

};
export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"createpagecontrols"}}>{`createPageControls`}</MDXTag>
<MDXTag name="p" components={components}>{`The `}<MDXTag name="inlineCode" components={components} parentName="p">{`createPageControls`}</MDXTag>{` function returns a hook, `}<MDXTag name="inlineCode" components={components} parentName="p">{`usePageControls`}</MDXTag>{`, that you
can use to control a page component. This hook provides an `}<MDXTag name="strong" components={components} parentName="p">{`alternative`}</MDXTag>{` to
PageController.`}</MDXTag>
<MDXTag name="p" components={components}>{`Follow the steps below to get started. The `}<MDXTag name="inlineCode" components={components} parentName="p">{`usePageControls`}</MDXTag>{` section documents
the `}<MDXTag name="inlineCode" components={components} parentName="p">{`usePageControls`}</MDXTag>{` hook in more detail. At the end of this page, you'll find
a complete example that uses each method.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"usage"}}>{`Usage`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"1-create-the-hook"}}><MDXTag name="strong" components={components} parentName="h3">{`1.`}</MDXTag>{` Create the hook`}</MDXTag>
<MDXTag name="p" components={components}>{`In an overrides file, import `}<MDXTag name="inlineCode" components={components} parentName="p">{`createPageControls`}</MDXTag>{` from the `}<MDXTag name="inlineCode" components={components} parentName="p">{`framer-controller`}</MDXTag>{`
library as shown below. Next, call `}<MDXTag name="inlineCode" components={components} parentName="p">{`createPageControls`}</MDXTag>{` and store the returned
hook under the name `}<MDXTag name="inlineCode" components={components} parentName="p">{`usePageControls`}</MDXTag>{`.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-tsx"}}>{`import { Override } from 'framer'
import { createPageControls } from 'framer-controller'

const usePageControls = createPageControls()
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`If you wish, you can also pass `}<MDXTag name="inlineCode" components={components} parentName="p">{`createPageControls`}</MDXTag>{` an object of (optional)
options.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-tsx"}}>{`const usePageControls = createPageControls({
    loop: true,
    currentPage: 2,
    history: [1, 2, 5, 2],
})
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"2-connect-a-page-component"}}><MDXTag name="strong" components={components} parentName="h3">{`2.`}</MDXTag>{` Connect a Page component`}</MDXTag>
<MDXTag name="p" components={components}>{`Next, override the page component you wish to control. Call `}<MDXTag name="inlineCode" components={components} parentName="p">{`usePageControls`}</MDXTag>{`
inside of the override and pass the returned values `}<MDXTag name="inlineCode" components={components} parentName="p">{`currentPage`}</MDXTag>{` and
`}<MDXTag name="inlineCode" components={components} parentName="p">{`onChangePage`}</MDXTag>{` to the component.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-tsx"}}>{`export function PageComponent(props): Override {
    const { currentPage, onChangePage } = usePageControls(props)

    return {
        currentPage,
        onChangePage,
    }
}
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"3-control-the-page-component"}}><MDXTag name="strong" components={components} parentName="h3">{`3.`}</MDXTag>{` Control the Page component`}</MDXTag>
<MDXTag name="p" components={components}>{`From here on, you can control the Page component in any other override by
calling the `}<MDXTag name="inlineCode" components={components} parentName="p">{`usePageControls`}</MDXTag>{` hook from your override and using the properties
and methods that it returns.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-tsx"}}>{`export function NextButton(props): Override {
    const { snapToNextPage } = usePageControls()

    return {
        onTap: () => snapToNextPage(),
    }
}
`}</MDXTag></MDXTag>
<MDXTag name="h1" components={components} props={{"id":"usepagecontrols"}}>{`usePageControls`}</MDXTag>
<MDXTag name="p" components={components}>{`Once you've created a `}<MDXTag name="inlineCode" components={components} parentName="p">{`usePageControls`}</MDXTag>{` hook (using `}<MDXTag name="inlineCode" components={components} parentName="p">{`createPageControls`}</MDXTag>{`), you
can call `}<MDXTag name="inlineCode" components={components} parentName="p">{`usePageControls`}</MDXTag>{` from inside of an override. Doing do will return a
collection of `}<MDXTag name="strong" components={components} parentName="p">{`properties`}</MDXTag>{`, `}<MDXTag name="strong" components={components} parentName="p">{`event handlers`}</MDXTag>{` and `}<MDXTag name="strong" components={components} parentName="p">{`methods`}</MDXTag>{` for controlling
the Page component or interpreting its state.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"usage-1"}}>{`Usage`}</MDXTag>
<MDXTag name="p" components={components}>{`At minimum, you should call this hook from your Page component's override as
shown below. This will connect your Page component's `}<MDXTag name="inlineCode" components={components} parentName="p">{`currentPage`}</MDXTag>{` to the hook's
`}<MDXTag name="inlineCode" components={components} parentName="p">{`currentPage`}</MDXTag>{`, as well as guarantee that these stay in sync as the user swipes
between pages.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-tsx"}}>{`export function PageComponent(props): Override {
    const { currentPage, onChangePage } = usePageControls(props)

    return {
        currentPage,
        onChangePage,
    }
}
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Note that in the example above, we're calling the `}<MDXTag name="inlineCode" components={components} parentName="p">{`usePageControls`}</MDXTag>{` hook with
the `}<MDXTag name="inlineCode" components={components} parentName="p">{`props`}</MDXTag>{` of the Page component. You only have to do this with the Page
component's props. In the other examples, we call this hook without any
argument.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"properties"}}>{`Properties`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"-currentpage-number"}}>{` currentPage `}<MDXTag name="inlineCode" components={components} parentName="h3">{`number`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`The component's current page (as determined by the hook).`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-tsx"}}>{`export function PageNumber(props): Override {
    const { currentPage } = usePageControls()

    return {
        text: currentPage,
    }
}
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"event-handlers"}}>{`Event Handlers`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"-onchangepage-currentpage-number--void"}}>{` onChangePage `}<MDXTag name="inlineCode" components={components} parentName="h3">{`(currentPage: number) => void`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Updates the hook when the user changes the Page component's current page.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-tsx"}}>{`export function PageComponent(props): Override {
    const { currentPage, onChangePage } = usePageControls(props)

    return {
        currentPage,
        onChangePage,
    }
}
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Though you can pass this property directly to your Page component's
`}<MDXTag name="inlineCode" components={components} parentName="p">{`onChangePage`}</MDXTag>{` prop, you mmight want to do other things in your component's
`}<MDXTag name="inlineCode" components={components} parentName="p">{`onChangePage`}</MDXTag>{` event, too. In that case, you can also call this method like
this:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-tsx"}}>{`export function PageComponent(props): Override {
    const { currentPage, onChangePage } = usePageControls(props)

    return {
        currentPage,
        onChangePage: (currentPage) => {
            // other stuff
            onChangePage(currentPage)
        },
    }
}
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"methods"}}>{`Methods`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"-snaptopage-index-number--0--void"}}>{` snapToPage `}<MDXTag name="inlineCode" components={components} parentName="h3">{`(index: number = 0) => void`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Snaps the page component to the page at the provided index. Defaults to `}<MDXTag name="inlineCode" components={components} parentName="p">{`0`}</MDXTag>{`.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-tsx"}}>{`export function Page2Button(props): Override {
    const { snapToPage, nextPage } = usePageControls()

    return {
        onTap: () => snapToPage(2),
    }
}
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"-snaptonextpage-direction-right--left--right--void"}}>{` snapToNextPage `}<MDXTag name="inlineCode" components={components} parentName="h3">{`(direction: 'right' | 'left' = 'right') => void`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Snaps the page component to the next page in a given direction, either `}<MDXTag name="inlineCode" components={components} parentName="p">{`"right"`}</MDXTag>{`
or `}<MDXTag name="inlineCode" components={components} parentName="p">{`"left"`}</MDXTag>{`. Defaults to `}<MDXTag name="inlineCode" components={components} parentName="p">{`"right"`}</MDXTag>{`.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-tsx"}}>{`export function RightButton(props): Override {
    const { snapToNextPage } = usePageControls()

    return {
        onTap: () => snapToNextPage(),
    }
}

export function LeftButton(props): Override {
    const { snapToNextPage } = usePageControls()

    return {
        onTap: () => snapToNextPage('left'),
    }
